module.exports = {
    meta: {
        type: "suggestion",
        docs: {
            description: "enforce use of dataStore getters and setters",
        },
        fixable: false,
    },
    create(context) {
        return {
            Identifier(node) {
                if (node.parent && node.parent.type === 'ImportDefaultSpecifier') {
                    if (node.name == '_cloneDeep') {
                        context.report(node, 'use datastore getter and setter rather than _cloneDeep');
                    }
                    return;
                }
            }
        }
    }
}
