module.exports = {
  meta: {
    docs: {
      description: "Prefer other functional constructs like map(), reduce(), for...of instead of Array.forEach",
      category: "UC Best Practices",
      recommended: false
    },
    fixable: null,
  },

  create: function (context) {
    return {
      CallExpression(node) {
        if (node.callee.property && node.callee.property.name === 'forEach') {
          context.report(node, 'Prefer other functional constructs like map(), reduce(), for...of instead of Array.forEach');
        }
      }
    };
  }
};
