module.exports = {
  meta: {
    fixable: false,
  },
  create(context) {
    return {
      Identifier(node) {
        // Function name should be camelCase
        if (node.parent
            && node.parent.type === 'ClassDeclaration') {
            context.report(node, 'Class creation is not allowed');
          return;
        }
      }
    };
  }
};