module.exports = {
  meta: {
    type: "suggestion",
    docs: {
      description: "Prefer async/await to Promise",
      category: "UC Best Practices",
      recommended: false
    },
    fixable: null
  },

  create: function (context) {
    return {
      MemberExpression(node) {
        if (node.property && node.property.name === 'then') {
          context.report(node.property, 'Prefer async/await to Promise');
        } else if (node.property && node.property.name === 'catch') {
          context.report(node.property, 'Prefer async/await to Promise');
        }
      }
    };
  }
};
