const rule = require('../../../lib/rules/file-name');
const RuleTester = require('eslint').RuleTester;
const ruleTester = new RuleTester();
const message = "Filename 'README' is not in snake_case.";

ruleTester.run('file-name', rule, {

    valid: [
        {
            code: 'var barFoo = "bar"',
            filename: 'lib/rules/case.js',
        }
    ],
    invalid: [
        {
            code: 'var barFoo = "bar"',
            filename: 'README.md',
            parserOptions: { ecmaVersion: 2020 },
            errors: [{ message }]
        }
    ]
});
