const rule = require('../../../lib/rules/import-clone-deep-mf');
const RuleTester = require('eslint').RuleTester;
const ruleTester = new RuleTester();
const message = "use datastore getter and setter rather than _cloneDeep";

ruleTester.run('import-clone-deep-mf', rule, {

    valid: [
        {
            code: 'import _get from "lodash-es/get"',
            parserOptions: { ecmaVersion: 2020, sourceType:'module' },
        }
    ],
    invalid: [
        {
            code: 'import _cloneDeep from "lodash-es/cloneDeep"',
            parserOptions: { ecmaVersion: 2020, sourceType: 'module' },
            errors: [{ message }]
        }
    ]
});
