const rule = require('../../../lib/rules/readme-mf');
const RuleTester = require('eslint').RuleTester;
const ruleTester = new RuleTester();
const message = "Filename 'README' is not present";

ruleTester.run('readme-mf', rule, {

    valid: [
        {
            code: 'var barFoo = "bar"',
            filename: 'micro_frontends/rules/README.md',
        }
    ],
    invalid: [
        {
            code: 'var barFoo = "bar"',
            filename: 'micro_frontends/rules/case.js',
            parserOptions: { ecmaVersion: 2020 },
            errors: [{ message }]
        }
    ]
});
